import React from 'react';
import {
  Text,
  Image,
} from 'react-native';
import styled from 'styled-components/native';
import CheckIcon from './icons/CheckIcon';


export default function PageHeader({ title, subtitle }) {
  let subtitleComponent;
  if (subtitle) {
    subtitleComponent = <Text>{subtitle}</Text>;
  }

  return (
    <>
      <CheckIcon />
      <Title>{title}</Title>
      {subtitleComponent}
    </>
  );
}

const Title = styled.Text`
  font-family: MarkPro;
  font-size: 22px;
  font-weight: bold;
  font-stretch: normal;
  font-style: normal;
  color: #1f2a4b;
  margin: 25px 168px 6px 0;
`;
