import React, { useState } from 'react';
import {
  TextInput,
} from 'react-native';
import styled from 'styled-components/native';


export default function Input({ placeholder, onChangeText, validate }) {
  const [borderColor, setBorderColor] = useState('#e1e1e1');
  const [hasError, setHasError] = useState(false);
  const [hasFocus, setHasFocus] = useState(false);

  const handleFocus = (borderColor) => {
    setBorderColor(borderColor); 
    setHasFocus(true);
  }

  const borderColors = {
    focus: '#4a77e5',
    blur: '#e1e1e1',
    error: 'red',
  };

  const handleBlur = (borderColor) => {
    setBorderColor(hasError ? borderColor : 'red');
    setHasFocus(false);
  }

  const handleTextChange = (text) => {
    onChangeText(text);
    let isValid = validate(text);
    setHasError(isValid);
    if (!isValid) {
      setBorderColor(borderColors.error);
    } else if (hasFocus) {
      setBorderColor(borderColors.focus);
    } else {
      setBorderColor(borderColors.blur);
    }
  }

  return (
    <>
    <CustomInput
      placeholder={placeholder}
      placeholderTextColor="#9ea3b2"
      borderColor={borderColor}
      onFocus={() => { handleFocus('#4a77e5'); }}
      onBlur={() => { handleBlur('#e1e1e1'); }}
      onChangeText={handleTextChange} />
    </>
  );
}

const CustomInput = styled.TextInput`
  padding: 0.5rem 0 0.5rem 0;
  font-family: mark-pro-regular; 
  font-size: 16px;
  font-weight: normal;
  font-stretch: normal;
  font-style: normal;
  line-height: normal;
  letter-spacing: normal;
  border: 0;
  borderBottomWidth: 1px;
  borderBottomColor: ${(props) => props.borderColor};
  outlineWidth: 0;
`;
